mapper.select:
SELECT *
FROM meal_intakes
WHERE user_id = #{userId}
  AND meal_plan_day_id = #{mealPlanDayId}

mapper.delete:
DELETE FROM meal_intakes
WHERE user_id = #{userId}
  AND meal_plan_day_id = #{mealPlanDayId}

DELETE FROM meal_intakes
WHERE user_id = #{userId}
  AND meal_plan_day_id = #{mealPlanDayId}
  AND meal_time = #{mealTime}

mapper.select:
SELECT *
FROM meal_intakes
WHERE user_id = #{userId}
  AND meal_plan_day_id = #{mealPlanDayId}
  AND is_consumed = 1
